const createdScore = Math.max(0, 100 - daysSinceCreated);
const updatedScore = Math.max(0, 50 - daysSinceUpdated);
const activityScore = Math.min(repo.stargazers_count / 10, 50);
const freshnessScore = createdScore + updatedScore + activityScore;

const repoText = `${repo.name} ${repo.description || ''} ${(repo.topics || []).join(' ')}`.toLowerCase();

const categories = {
  'breakthrough-research': ['paper', 'research', 'arxiv', 'sota', 'state-of-the-art', 'novel'],
  'production-tools': ['deployment', 'production', 'scale', 'enterprise', 'docker', 'kubernetes'],
  'learning-resources': ['tutorial', 'course', 'learn', 'education', 'beginner', 'guide'],
  'experimental-ai': ['experimental', 'prototype', 'beta', 'alpha', 'demo', 'proof-of-concept'],
  'data-tools': ['data', 'dataset', 'preprocessing', 'cleaning', 'etl', 'pipeline'],
  'model-implementations': ['implementation', 'model', 'algorithm', 'neural-network', 'transformer'],
  'machine-learning': ['ml', 'machine-learning', 'scikit-learn', 'sklearn', 'xgboost', 'lightgbm'],
  'deep-learning': ['deep-learning', 'neural-network', 'pytorch', 'tensorflow', 'keras', 'jax'],
  'nlp': ['nlp', 'natural-language', 'transformer', 'bert', 'gpt', 'llm', 'langchain'],
  'computer-vision': ['computer-vision', 'opencv', 'image-processing', 'yolo', 'segmentation'],
  'generative-ai': ['stable-diffusion', 'generative', 'gan', 'diffusion', 'dalle']
};

let detectedCategory = 'general-ai';
