for (const [category, keywords] of Object.entries(categories)) {
  const score = keywords.reduce((sum, keyword) => sum + (repoText.includes(keyword) ? 1 : 0), 0);
  if (score > bestScore) {
    bestScore = score;
    detectedCategory = category;
  }
}

